import React, { useState } from "react";

const PrescriptionsPage = () => {
  const [prescriptions, setPrescriptions] = useState([]);
  const [form, setForm] = useState({ patient: "", medication: "", dosage: "", instructions: "" });

  const handleSubmit = (e) => {
    e.preventDefault();
    setPrescriptions([...prescriptions, form]);
    setForm({ patient: "", medication: "", dosage: "", instructions: "" });
  };

  return (
    <div style={{ padding: "1rem" }}>
      <h2>Prescriptions</h2>
      <form onSubmit={handleSubmit} style={{ marginBottom: "1rem" }}>
        <input
          type="text"
          placeholder="Patient Name"
          value={form.patient}
          onChange={(e) => setForm({ ...form, patient: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Medication"
          value={form.medication}
          onChange={(e) => setForm({ ...form, medication: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Dosage"
          value={form.dosage}
          onChange={(e) => setForm({ ...form, dosage: e.target.value })}
          required
        />
        <textarea
          placeholder="Instructions"
          value={form.instructions}
          onChange={(e) => setForm({ ...form, instructions: e.target.value })}
          required
        />
        <button type="submit">Create Prescription</button>
      </form>

      <ul>
        {prescriptions.map((p, i) => (
          <li key={i}>
            <strong>{p.patient}</strong>: {p.medication} ({p.dosage}) â€” {p.instructions}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PrescriptionsPage;
