import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";

const PatientDashboard = () => {
  const { logout } = useAuth();
  const [activeSection, setActiveSection] = useState("appointments");

  // Render content based on active menu
  const renderContent = () => {
    switch (activeSection) {
      case "appointments":
        return <div><h3>Your Appointments</h3><p>List of appointments will go here.</p></div>;
      case "medicalRecords":
        return <div><h3>Medical Records</h3><p>Medical records details will go here.</p></div>;
      case "profile":
        return <div><h3>Profile</h3><p>Patient profile info here.</p></div>;
      default:
        return null;
    }
  };

  return (
    <div>
      <header style={{ display: "flex", justifyContent: "space-between", padding: "1rem", backgroundColor: "#e0e0e0" }}>
        <div><strong>Kadaji Hospital</strong></div>
        <button onClick={logout}>Sign Out</button>
      </header>
      <aside style={{ float: "left", width: "200px", padding: "1rem", backgroundColor: "#f4f4f4" }}>
        <p 
          style={{ cursor: "pointer", fontWeight: activeSection === "appointments" ? "bold" : "normal" }} 
          onClick={() => setActiveSection("appointments")}
        >
          Appointments
        </p>
        <p 
          style={{ cursor: "pointer", fontWeight: activeSection === "medicalRecords" ? "bold" : "normal" }} 
          onClick={() => setActiveSection("medicalRecords")}
        >
          Medical Records
        </p>
        <p 
          style={{ cursor: "pointer", fontWeight: activeSection === "profile" ? "bold" : "normal" }} 
          onClick={() => setActiveSection("profile")}
        >
          Profile
        </p>
      </aside>
      <main style={{ marginLeft: "210px", padding: "1rem" }}>
        <h2>Patient Dashboard</h2>
        {renderContent()}
      </main>
    </div>
  );
};

export default PatientDashboard;
